package com.automation.ui.digitaldeployer.pageobjectsfactory.pageobject.index.pages;/** * @author */import com.automation.ui.base.common.core.element.dropdown.DropDownHelper;import com.automation.ui.digitaldeployer.pageobjectsfactory.pageobject.base.ProjectBasePageObject;import com.automation.ui.digitaldeployer.pageobjectsfactory.pageobject.index.vo.InitialSearchVO;import com.automation.ui.digitaldeployer.pageobjectsfactory.pageobject.index.xpathconstants.IndexConstants;import org.apache.log4j.Logger;import org.openqa.selenium.WebElement;import org.openqa.selenium.support.FindBy;import com.automation.ui.base.common.core.assertion.Assertion;import com.automation.ui.base.common.prpreaders.AssertDataReader;public class IndexPage extends ProjectBasePageObject {    private static Logger logger = Logger.getLogger(IndexPage.class);    @FindBy(xpath = IndexConstants.SEARCHINDUSTRY)    private WebElement searchIndustry;    @FindBy(xpath = IndexConstants.SEARCHMARKETNEED)    private WebElement searchMarketNeed;    @FindBy(xpath = IndexConstants.SEARCHBUSINESSGOAL)    private WebElement searchBusiGoal;    @FindBy(xpath = IndexConstants.SEARCHLEADMANAGEMENT)    private WebElement searchPick;    @FindBy(xpath = IndexConstants.CONTINUE)    private WebElement continueButton;    @FindBy(xpath = IndexConstants.CCLINK)    private WebElement ccLink;    @FindBy(xpath = IndexConstants.CCDISMISS)    private WebElement ccDisMiss;    @FindBy(xpath = IndexConstants.ACCPRODUCT)    private WebElement accProduct;    @FindBy(xpath = IndexConstants.BLOGLINK)    private WebElement blogLink;    @FindBy(xpath = IndexConstants.SECABOUT)    private WebElement secAbout;    @FindBy(xpath = IndexConstants.SIGNUP)    private WebElement signUp;    @FindBy(xpath = IndexConstants.PRODUCT)    private WebElement product;    @FindBy(xpath = IndexConstants.ABOUT)    private WebElement about;    @FindBy(xpath = IndexConstants.CONTACT)    private WebElement contact;    @FindBy(xpath = IndexConstants.PRIVACY)    private WebElement privacy;    @FindBy(xpath = IndexConstants.TERMS)    private WebElement terms;    @FindBy(xpath = IndexConstants.DDLINKEDIN)    private WebElement cuspLinkedin;    @FindBy(xpath = IndexConstants.DDTWITTER)    private WebElement cuspTwitter;    @FindBy(xpath = IndexConstants.MAILTO)    private WebElement mailTo;    @FindBy(xpath = IndexConstants.LINKEDINBUTTON)    private WebElement linkedInButton;    @FindBy(xpath = IndexConstants.CONTASGUEST)    private WebElement contAsGuest;    @FindBy(xpath = IndexConstants.REGISTER)    private WebElement register;    @FindBy(xpath = IndexConstants.CLOSEMODEL)    private WebElement closeModel;    @FindBy(xpath = IndexConstants.CLOSE)    private WebElement close;    public IndexPage open() {        logger.debug("open the URL" + getCurrentUrl());        getUrl(getCurrentUrl());        return this;    }    public void continueButton() {        try {         waitAndClick(continueButton);        } catch (Exception e) {            Assertion.fail(AssertDataReader.assertreader.getValue("DDASSERTMSG_ASSERT_ERROR"));            e.printStackTrace();        }    }    public void searchIndustry(InitialSearchVO search) {        try {            DropDownHelper dh=new DropDownHelper(  driver, this);            dh.selectDropDown(searchIndustry, search.getSelectIndustry().getIndustry());        } catch (Exception e) {            Assertion.fail(AssertDataReader.assertreader.getValue("DDASSERTMSG_ASSERT_ERROR"));             e.printStackTrace();        }    }    public void searchMarketing(InitialSearchVO search) {        try {            fillInputAfterClear(searchMarketNeed,"lead");            DropDownHelper dh=new DropDownHelper(  driver, this);            dh.selectDropDown(searchPick, "lead management");        } catch (Exception e) {            Assertion.fail(AssertDataReader.assertreader.getValue("DDASSERTMSG_ASSERT_ERROR"));            e.printStackTrace();        }    }    public void searchGoal(InitialSearchVO search) {        try {            DropDownHelper dh=new DropDownHelper(  driver, this);            dh.selectDropDown(searchIndustry, search.getSelectBusinessGoal().getGoal());            waitAndClick(searchBusiGoal);        } catch (Exception e) {            Assertion.fail(AssertDataReader.assertreader.getValue("DDASSERTMSG_ASSERT_ERROR"));            e.printStackTrace();        }    }    public void search(InitialSearchVO search) {        try {            DropDownHelper dh=new DropDownHelper(  driver, this);            dh.selectDropDown(searchIndustry, search.getSelectIndustry().getIndustry());            fillInputAfterClear(searchMarketNeed,"lead");            dh=new DropDownHelper(  driver, this);            dh.selectDropDown(searchPick, "lead management");            Thread.sleep(2000);            dh=new DropDownHelper(  driver, this);            dh.selectDropDown(searchBusiGoal, search.getSelectBusinessGoal().getGoal());        } catch (Exception e) {            Assertion.fail(AssertDataReader.assertreader.getValue("DDASSERTMSG_ASSERT_ERROR"));            e.printStackTrace();        }    }    public void ccLink() {        try {            waitAndClick(ccLink);        } catch (Exception e) {            Assertion.fail(AssertDataReader.assertreader.getValue("DDASSERTMSG_ASSERT_ERROR"));            e.printStackTrace();        }    }    public void ccDisMiss() {        try {            waitAndClick(ccDisMiss);        } catch (Exception e) {            Assertion.fail(AssertDataReader.assertreader.getValue("DDASSERTMSG_ASSERT_ERROR"));            e.printStackTrace();        }    }    public void accProduct() {        try {            waitAndClick(accProduct);        } catch (Exception e) {            Assertion.fail(AssertDataReader.assertreader.getValue("DDASSERTMSG_ASSERT_ERROR"));            e.printStackTrace();        }    }    public void blogLinks() {        try {            waitAndClick(blogLink);        } catch (Exception e) {            Assertion.fail(AssertDataReader.assertreader.getValue("DDASSERTMSG_ASSERT_ERROR"));            e.printStackTrace();        }    }    public void secAbout() {        try {            waitAndClick(secAbout);        } catch (Exception e) {            Assertion.fail(AssertDataReader.assertreader.getValue("DDASSERTMSG_ASSERT_ERROR"));            e.printStackTrace();        }    }    public void signUp() {        try {            waitAndClick(signUp);        } catch (Exception e) {            Assertion.fail(AssertDataReader.assertreader.getValue("DDASSERTMSG_ASSERT_ERROR"));            e.printStackTrace();        }    }}